// components/Hero.jsx
import React from 'react';
import { motion } from 'framer-motion';
import TypingAnimation from './TypingAnimation';
import Particles from './Particles';

const Hero = () => {
  return (
    <section id="home" className="min-h-screen relative flex items-center justify-center overflow-hidden bg-gradient-to-br from-black via-gray-900 to-black">
      <Particles quantity={30} />
      
      <div className="container mx-auto px-4 z-10">
        <motion.div 
          initial={{ opacity: 0, y: 40 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1.2, ease: "easeOut" }}
          className="text-center"
        >
          <motion.div 
            initial={{ scale: 0, rotate: -180 }}
            animate={{ scale: 1, rotate: 0 }}
            transition={{ duration: 1.5, type: "spring", stiffness: 100 }}
            className="mb-12 mx-auto w-24 h-24 rounded-full border-2 border-gray-600 bg-gray-900/50 flex items-center justify-center shadow-2xl shadow-gray-800/20"
          >
            <div className="w-16 h-16 rounded-full border border-gray-600 flex items-center justify-center">
              <span className="text-white font-semibold text-2xl">MAB</span>
            </div>
          </motion.div>
          
          <motion.h1 
            className="text-5xl md:text-7xl lg:text-8xl font-semibold mb-6 text-white tracking-tight"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 1, delay: 0.3 }}
          >
            Muhammad
            <br />
            <span className="text-gray-300 font-medium">
              Abdul Bari
            </span>
          </motion.h1>
          
          <motion.div 
            initial={{ width: 0 }}
            animate={{ width: '200px' }}
            transition={{ duration: 1.5, delay: 0.8 }}
            className="mx-auto h-px bg-gradient-to-r from-transparent via-gray-600 to-transparent mb-8"
          ></motion.div>
          
          <motion.div 
            className="text-xl md:text-2xl mb-12 text-gray-300 font-light tracking-wide"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 1, delay: 1 }}
          >
            <div className="flex items-center justify-center gap-4">
              <div className="w-4 h-px bg-gray-600 opacity-60"></div>
              <TypingAnimation
                strings={[
                  "Web & App Developer",
                  "Software Architect",
                  "Digital Craftsman",
                  "Visionary Engineer"
                ]}
                typeSpeed={60}
                backSpeed={40}
                className="text-gray-300 font-medium"
              />
              <div className="w-4 h-px bg-gray-600 opacity-60"></div>
            </div>
          </motion.div>
          
          <motion.div 
            className="flex flex-col sm:flex-row justify-center gap-6 items-center"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 1.2 }}
          >
            <motion.a 
              href="#projects" 
              whileHover={{ 
                scale: 1.05,
                boxShadow: "0 10px 30px rgba(255, 255, 255, 0.1)"
              }}
              whileTap={{ scale: 0.98 }}
              className="px-12 py-4 rounded-sm bg-gray-900 border border-gray-700 text-white font-medium tracking-wider uppercase text-sm hover:border-gray-600 transition-all duration-500 group relative overflow-hidden"
            >
              <span className="relative z-10">View Portfolio</span>
              <div className="absolute inset-0 bg-gray-800 opacity-0 group-hover:opacity-10 transition-opacity duration-500"></div>
            </motion.a>
            
            <motion.a 
              href="/cv.pdf" 
              download
              whileHover={{ 
                scale: 1.05,
                boxShadow: "0 10px 30px rgba(255, 255, 255, 0.1)"
              }}
              whileTap={{ scale: 0.98 }}
              className="px-12 py-4 rounded-sm border border-gray-600 bg-gray-800/30 text-white font-medium tracking-wider uppercase text-sm hover:bg-gray-800/50 transition-all duration-500 group relative overflow-hidden"
            >
              <span className="relative z-10">Download CV</span>
              <div className="absolute inset-0 bg-gray-700 opacity-0 group-hover:opacity-10 transition-opacity duration-500"></div>
            </motion.a>
          </motion.div>
        </motion.div>
      </div>
      
      <motion.div 
        className="absolute bottom-10 left-1/2 transform -translate-x-1/2"
        animate={{ y: [0, 10, 0] }}
        transition={{ duration: 2, repeat: Infinity }}
      >
        <div className="flex flex-col items-center">
          <div className="text-gray-500 text-xs tracking-widest uppercase mb-2 opacity-70 font-medium">
            Scroll
          </div>
          <div className="w-px h-12 bg-gradient-to-b from-gray-500 to-transparent"></div>
        </div>
      </motion.div>
      
      <div className="absolute top-8 left-8 w-16 h-16 border-t-2 border-l-2 border-gray-700/30"></div>
      <div className="absolute top-8 right-8 w-16 h-16 border-t-2 border-r-2 border-gray-700/30"></div>
      <div className="absolute bottom-8 left-8 w-16 h-16 border-b-2 border-l-2 border-gray-700/30"></div>
      <div className="absolute bottom-8 right-8 w-16 h-16 border-b-2 border-r-2 border-gray-700/30"></div>
    </section>
  );
};

export default Hero;
