import React from 'react';
import { motion } from 'framer-motion';

const About = () => {
  return (
    <section id="about" className="py-20 relative overflow-hidden bg-gradient-to-br from-[#FAF8F5] to-[#F0ECE5]">
      <div className="absolute inset-0 luxury-grid opacity-30"></div>
      
      <div className="container mx-auto px-4 relative z-10">
        <motion.div 
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
          className="text-center mb-16"
        >
          <h2 className="text-4xl  font-semibold mb-4 text-luxury-charcoal">About Me</h2>
          <div className="ornamental-divider w-48 mx-auto"></div>
        </motion.div>
        
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 items-center">
          <motion.div 
            initial={{ opacity: 0, x: -50 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
            className="relative"
          >
            <div className="luxury-glass rounded-2xl p-8 shadow-xl">
              <div className="text-lg text-luxury-charcoal/80 leading-relaxed space-y-6">
                <p className="serif-font text-xl text-gold-700 mb-4">
                  "Crafting digital excellence with timeless elegance"
                </p>
                <p>
                  I'm a 16-year-old Software Engineering student with a passion for creating 
                  innovative digital solutions. As an INTP personality type, I thrive on 
                  solving complex problems and building systems that make a difference.
                </p>
                <p>
                  My journey in development started at a young age, and I've since mastered 
                  various technologies across web and mobile development. I specialize in 
                  creating responsive, user-friendly applications with clean, efficient code.
                </p>
                <p>
                  When I'm not coding, you can find me exploring new technologies, 
                  contributing to open-source projects, or brainstorming my next big idea.
                </p>
              </div>
            </div>
            
            <div className="absolute -top-4 -left-4 w-24 h-24 rounded-full bg-gold-300/20 blur-xl"></div>
            <div className="absolute -bottom-4 -right-4 w-32 h-32 rounded-full bg-gold-400/20 blur-xl"></div>
          </motion.div>
          
          <motion.div 
            initial={{ opacity: 0, x: 50 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
            viewport={{ once: true }}
            className="grid grid-cols-2 gap-4"
          >
            {[
              { name: 'HTML/CSS', level: 95, color: 'from-gold-500 to-gold-600' },
              { name: 'JavaScript', level: 90, color: 'from-gold-500 to-gold-600' },
              { name: 'React.js', level: 85, color: 'from-gold-500 to-gold-600' },
              { name: 'Node.js', level: 80, color: 'from-gold-500 to-gold-600' },
              { name: 'Flutter', level: 75, color: 'from-gold-400 to-gold-500' },
              { name: 'Laravel', level: 70, color: 'from-gold-400 to-gold-500' },
              { name: 'ASP.NET', level: 65, color: 'from-gold-400 to-gold-500' },
              { name: 'MongoDB', level: 85, color: 'from-gold-500 to-gold-600' },
            ].map((skill, index) => (
              <div key={index} className="luxury-glass rounded-xl p-4 hover:shadow-gold transition-all duration-300 group">
                <div className="flex justify-between mb-2">
                  <span className="text-luxury-charcoal font-medium group-hover:text-gold-600 transition-colors">{skill.name}</span>
                  <span className="text-gold-600 font-semibold">{skill.level}%</span>
                </div>
                <div className="w-full bg-white/50 rounded-full h-2">
                  <div 
                    className={`h-2 rounded-full bg-gradient-to-r ${skill.color}`}
                    style={{ width: `${skill.level}%` }}
                  ></div>
                </div>
              </div>
            ))}
          </motion.div>
        </div>
      </div>
    </section>
  );
};

export default About;
