// components/Hero.js
import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import TypingAnimation from './TypingAnimation';

const Hero = () => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  
  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePosition({ x: e.clientX, y: e.clientY });
    };
    
    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  const backgroundStyle = {
    background: `radial-gradient(600px circle at ${mousePosition.x}px ${mousePosition.y}px, rgba(201, 168, 107, 0.06), transparent 40%)`,
  };

  return (
    <section id="home" className="min-h-screen relative flex items-center justify-center overflow-hidden bg-gradient-to-br from-[#0A0A0A] via-[#111111] to-[#0A0A0A]">
      
      {/* Animated background elements */}
      <div className="absolute inset-0" style={backgroundStyle}></div>
      
      {/* Geometric patterns */}
      <div className="absolute inset-0 opacity-10">
        <div className="absolute top-1/4 left-1/4 w-72 h-72 border border-gold-600/20 rounded-full"></div>
        <div className="absolute bottom-1/3 right-1/3 w-48 h-48 border border-gold-600/20 rotate-45"></div>
        <div className="absolute top-1/3 right-1/4 w-32 h-32 border border-gold-600/20 rotate-12"></div>
      </div>
      
      {/* Floating particles */}
      <div className="absolute inset-0">
        {[...Array(15)].map((_, i) => (
          <motion.div
            key={i}
            className="absolute w-1 h-1 bg-gold-600 rounded-full"
            style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
            }}
            animate={{
              y: [0, -20, 0],
              opacity: [0, 1, 0],
            }}
            transition={{
              duration: 3 + Math.random() * 5,
              repeat: Infinity,
              delay: Math.random() * 2,
            }}
          />
        ))}
      </div>
      
      <div className="container mx-auto px-4 z-10">
        <motion.div 
          initial={{ opacity: 0, y: 40 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1.2, ease: "easeOut" }}
          className="text-center"
        >
          {/* Animated Crest/Badge */}
          <motion.div 
            initial={{ scale: 0, rotate: -180 }}
            animate={{ scale: 1, rotate: 0 }}
            transition={{ duration: 1.5, type: "spring", stiffness: 100, delay: 0.2 }}
            className="mb-16 mx-auto w-28 h-28 rounded-full border border-gold-600/30 bg-black/50 flex items-center justify-center shadow-2xl shadow-gold-600/10 group relative"
          >
            <div className="absolute inset-0 rounded-full bg-gold-600/10 blur-md group-hover:blur-xl transition-all duration-700"></div>
            <div className="w-20 h-20 rounded-full border border-gold-600/50 flex items-center justify-center relative z-10">
              <span className="text-gold-600 font-semibold text-2xl tracking-widest">MAB</span>
            </div>
          </motion.div>
          
          {/* Name with enhanced animation */}
          <motion.h1 
            className="text-5xl md:text-7xl lg:text-8xl font-light mb-8 text-white tracking-tighter"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 1, delay: 0.5 }}
          >
            <span className="block">Muhammad</span>
            <motion.span 
              className="block bg-gradient-to-r from-gold-400 via-gold-500 to-gold-400 bg-clip-text text-transparent font-normal mt-2"
              animate={{
                backgroundPosition: ['0%', '200%'],
              }}
              transition={{
                duration: 5,
                repeat: Infinity,
                repeatType: "reverse",
              }}
            >
              Abdul Bari
            </motion.span>
          </motion.h1>
          
          {/* Animated Divider */}
          <motion.div 
            initial={{ width: 0 }}
            animate={{ width: '280px' }}
            transition={{ duration: 1.5, delay: 0.8 }}
            className="mx-auto h-px bg-gradient-to-r from-transparent via-gold-600/50 to-transparent mb-12 relative"
          >
            <motion.div 
              className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-3 h-3 rotate-45 bg-gold-600"
              animate={{ 
                rotate: 360,
                scale: [1, 1.2, 1]
              }}
              transition={{ 
                rotate: { duration: 4, repeat: Infinity, ease: "linear" },
                scale: { duration: 2, repeat: Infinity }
              }}
            />
          </motion.div>
          
          {/* Enhanced Tagline */}
          <motion.div 
            className="text-xl md:text-2xl mb-16 text-gray-300 font-light tracking-wider"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 1, delay: 1.2 }}
          >
            <div className="flex items-center justify-center gap-6">
              <motion.div 
                className="w-8 h-px bg-gold-600/60"
                initial={{ width: 0 }}
                animate={{ width: 24 }}
                transition={{ duration: 1, delay: 1.4 }}
              />
              <TypingAnimation
                strings={[
                  "Web & App Developer",
                  "Software Architect",
                  "Digital Craftsman",
                  "Visionary Engineer"
                ]}
                typeSpeed={60}
                backSpeed={40}
                className="text-gold-500 font-medium tracking-widest uppercase text-sm"
              />
              <motion.div 
                className="w-8 h-px bg-gold-600/60"
                initial={{ width: 0 }}
                animate={{ width: 24 }}
                transition={{ duration: 1, delay: 1.4 }}
              />
            </div>
          </motion.div>
          
          {/* Luxury Buttons with enhanced effects */}
          <motion.div 
            className="flex flex-col sm:flex-row justify-center gap-8 items-center"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 1.6 }}
          >
            <motion.a 
              href="#projects" 
              whileHover={{ 
                scale: 1.05,
                boxShadow: "0 10px 30px rgba(201, 168, 107, 0.3)",
                y: -5
              }}
              whileTap={{ scale: 0.98 }}
              className="px-14 py-4 rounded-sm bg-black/50 border border-gold-600/30 text-gold-500 font-medium tracking-widest uppercase text-xs hover:border-gold-600/60 transition-all duration-500 group relative overflow-hidden"
            >
              <span className="relative z-10">View Portfolio</span>
              <div className="absolute inset-0 bg-gold-600 opacity-0 group-hover:opacity-10 transition-opacity duration-500"></div>
              <div className="absolute inset-0 -z-10">
                <div className="absolute -inset-2 bg-gold-600/20 blur-md opacity-0 group-hover:opacity-100 transition-opacity duration-500"></div>
              </div>
            </motion.a>
            
            <motion.a 
              href="/cv.pdf" 
              download
              whileHover={{ 
                scale: 1.05,
                boxShadow: "0 10px 30px rgba(201, 168, 107, 0.2)",
                y: -5
              }}
              whileTap={{ scale: 0.98 }}
              className="px-14 py-4 rounded-sm border border-gold-600 bg-gold-600/10 text-gold-500 font-medium tracking-widest uppercase text-xs hover:bg-gold-600/20 transition-all duration-500 group relative overflow-hidden"
            >
              <span className="relative z-10">Download CV</span>
              <div className="absolute inset-0 bg-gold-600 opacity-0 group-hover:opacity-10 transition-opacity duration-500"></div>
              <div className="absolute inset-0 -z-10">
                <div className="absolute -inset-2 bg-gold-600/20 blur-md opacity-0 group-hover:opacity-100 transition-opacity duration-500"></div>
              </div>
            </motion.a>
          </motion.div>
        </motion.div>
      </div>
      
      {/* Enhanced Scroll Indicator */}
      <motion.div 
        className="absolute bottom-12 left-1/2 transform -translate-x-1/2"
        animate={{ y: [0, 12, 0] }}
        transition={{ duration: 2, repeat: Infinity }}
      >
        <div className="flex flex-col items-center">
          <div className="text-gold-600 text-xs tracking-widest uppercase mb-3 opacity-70 font-medium">
            Scroll
          </div>
          <div className="w-px h-16 bg-gradient-to-b from-gold-600 to-transparent"></div>
          <motion.div 
            className="w-1 h-3 bg-gold-600 rounded-full mt-1"
            animate={{ y: [0, 12, 0] }}
            transition={{ duration: 2, repeat: Infinity, delay: 0.5 }}
          />
        </div>
      </motion.div>
      
      {/* Luxury Corner Accents */}
      <div className="absolute top-10 left-10 w-20 h-20 border-t-2 border-l-2 border-gold-600/20"></div>
      <div className="absolute top-10 right-10 w-20 h-20 border-t-2 border-r-2 border-gold-600/20"></div>
      <div className="absolute bottom-10 left-10 w-20 h-20 border-b-2 border-l-2 border-gold-600/20"></div>
      <div className="absolute bottom-10 right-10 w-20 h-20 border-b-2 border-r-2 border-gold-600/20"></div>
    </section>
  );
};

export default Hero;
