import React from 'react';
import { motion } from 'framer-motion';

const Particles = ({ quantity = 30 }) => {
  const particles = Array.from({ length: quantity });
  
  return (
    <div className="absolute inset-0 overflow-hidden pointer-events-none">
      {particles.map((_, i) => {
        const size = Math.random() * 5 + 1;
        const posX = Math.random() * 100;
        const posY = Math.random() * 100;
        const delay = Math.random() * 20;
        const duration = 15 + Math.random() * 10;
        const color = `rgba(${
          Math.floor(Math.random() * 56) + 200
        }, ${
          Math.floor(Math.random() * 156) + 100
        }, 255, ${Math.random() * 0.4 + 0.1})`;
        
        return (
          <motion.div
            key={i}
            className="absolute rounded-full particle"
            style={{
              '--x': `${(Math.random() - 0.5) * 200}vw`,
              '--y': `${(Math.random() - 0.5) * 200}vh`,
              left: `${posX}%`,
              top: `${posY}%`,
              width: `${size}px`,
              height: `${size}px`,
              background: color,
              boxShadow: `0 0 ${size * 2}px ${size}px ${color}`,
            }}
            animate={{
              x: [(Math.random() - 0.5) * 100, (Math.random() - 0.5) * 200],
              y: [(Math.random() - 0.5) * 100, (Math.random() - 0.5) * 200],
              rotate: 360,
            }}
            transition={{
              duration: duration,
              repeat: Infinity,
              delay: delay,
              ease: "linear"
            }}
          />
        );
      })}
    </div>
  );
};

export default Particles;
