import React from 'react';
import { motion } from 'framer-motion';
import TypingAnimation from './TypingAnimation';
import Particles from './Particles';

const Hero = () => {
  return (
    <section id="home" className="min-h-screen relative flex items-center justify-center overflow-hidden bg-gradient-to-br from-[#FAF8F5] via-[#F5F2ED] to-[#EBE6DF]">
      
      {/* Simplified background pattern */}
      <div className="absolute inset-0 bg-[#FAF8F5] opacity-40"></div>
      
      {/* Gold Dust Particles */}
      <div className="absolute inset-0 bg-gradient-to-br from-transparent via-[#C9A86B]/10 to-transparent"></div>
      
      <Particles quantity={30} color="rgba(201, 168, 107, 0.3)" />
      
      <div className="absolute inset-0 bg-gradient-to-b from-transparent to-[#FAF8F5]/80"></div>
      
      <div className="container mx-auto px-4 z-10">
        <motion.div 
          initial={{ opacity: 0, y: 40 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1.2, ease: "easeOut" }}
          className="text-center"
        >
          {/* Crest/Badge */}
          <motion.div 
            initial={{ scale: 0, rotate: -180 }}
            animate={{ scale: 1, rotate: 0 }}
            transition={{ duration: 1.5, type: "spring", stiffness: 100 }}
            className="mb-12 mx-auto w-24 h-24 rounded-full border-2 border-[#C9A86B]/50 bg-white/80 flex items-center justify-center shadow-2xl shadow-[#C9A86B]/20"
          >
            <div className="w-16 h-16 rounded-full border border-[#C9A86B] flex items-center justify-center">
              <span className="text-[#C9A86B] font-semibold text-2xl">MAB</span>
            </div>
          </motion.div>
          
          {/* Name with Gold Leaf Effect */}
          <motion.h1 
            className="text-5xl md:text-7xl lg:text-8xl font-semibold mb-6 text-[#2C1810] tracking-tight"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 1, delay: 0.3 }}
          >
            Muhammad
            <br />
            <span className="bg-gradient-to-r from-[#C9A86B] via-[#D4B483] to-[#C9A86B] bg-clip-text text-transparent font-medium">
              Abdul Bari
            </span>
          </motion.h1>
          
          {/* Divider */}
          <motion.div 
            initial={{ width: 0 }}
            animate={{ width: '200px' }}
            transition={{ duration: 1.5, delay: 0.8 }}
            className="mx-auto h-px bg-gradient-to-r from-transparent via-[#C9A86B] to-transparent mb-8"
          ></motion.div>
          
          {/* Tagline with Gold Accent */}
          <motion.div 
            className="text-xl md:text-2xl mb-12 text-[#2C1810]/80 font-light tracking-wide"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 1, delay: 1 }}
          >
            <div className="flex items-center justify-center gap-4">
              <div className="w-4 h-px bg-[#C9A86B] opacity-60"></div>
              <TypingAnimation
                strings={[
                  "Web & App Developer",
                  "Software Architect",
                  "Digital Craftsman",
                  "Visionary Engineer"
                ]}
                typeSpeed={60}
                backSpeed={40}
                className="text-[#C9A86B] font-medium"
              />
              <div className="w-4 h-px bg-[#C9A86B] opacity-60"></div>
            </div>
          </motion.div>
          
          {/* Luxury Buttons */}
          <motion.div 
            className="flex flex-col sm:flex-row justify-center gap-6 items-center"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 1.2 }}
          >
            <motion.a 
              href="#projects" 
              whileHover={{ 
                scale: 1.05,
                boxShadow: "0 10px 30px rgba(201, 168, 107, 0.3)"
              }}
              whileTap={{ scale: 0.98 }}
              className="px-12 py-4 rounded-sm bg-white/90 border border-[#C9A86B]/50 text-[#C9A86B] font-medium tracking-wider uppercase text-sm hover:border-[#C9A86B]/60 transition-all duration-500 group relative overflow-hidden"
            >
              <span className="relative z-10">View Portfolio</span>
              <div className="absolute inset-0 bg-[#C9A86B] opacity-0 group-hover:opacity-5 transition-opacity duration-500"></div>
            </motion.a>
            
            <motion.a 
              href="/cv.pdf" 
              download
              whileHover={{ 
                scale: 1.05,
                boxShadow: "0 10px 30px rgba(201, 168, 107, 0.2)"
              }}
              whileTap={{ scale: 0.98 }}
              className="px-12 py-4 rounded-sm border border-[#C9A86B] bg-[#C9A86B]/10 text-[#C9A86B] font-medium tracking-wider uppercase text-sm hover:bg-[#C9A86B]/20 transition-all duration-500 group relative overflow-hidden"
            >
              <span className="relative z-10">Download CV</span>
              <div className="absolute inset-0 bg-[#C9A86B] opacity-0 group-hover:opacity-10 transition-opacity duration-500"></div>
            </motion.a>
          </motion.div>
        </motion.div>
      </div>
      
      {/* Scroll Indicator */}
      <motion.div 
        className="absolute bottom-10 left-1/2 transform -translate-x-1/2"
        animate={{ y: [0, 10, 0] }}
        transition={{ duration: 2, repeat: Infinity }}
      >
        <div className="flex flex-col items-center">
          <div className="text-[#C9A86B] text-xs tracking-widest uppercase mb-2 opacity-70 font-medium">
            Scroll
          </div>
          <div className="w-px h-12 bg-gradient-to-b from-[#C9A86B] to-transparent"></div>
        </div>
      </motion.div>
      
      {/* Luxury Corner Accents */}
      <div className="absolute top-8 left-8 w-16 h-16 border-t-2 border-l-2 border-[#C9A86B]/30"></div>
      <div className="absolute top-8 right-8 w-16 h-16 border-t-2 border-r-2 border-[#C9A86B]/30"></div>
      <div className="absolute bottom-8 left-8 w-16 h-16 border-b-2 border-l-2 border-[#C9A86B]/30"></div>
      <div className="absolute bottom-8 right-8 w-16 h-16 border-b-2 border-r-2 border-[#C9A86B]/30"></div>
    </section>
  );
};

export default Hero;
